[[module CSS]]

/* Wrath Theme */
/* (The World Looks Red) */
/* by syuzhet */


@import url('https://fonts.googleapis.com/css2?family=DM+Mono:ital,wght@0,300;0,400;0,500;1,300;1,400;1,500&amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;display=swap');
@import url('https://fonts.googleapis.com/css2?family=Playwrite+IS:wght@100..400&amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;display=swap');
@import url('https://fonts.googleapis.com/css2?family=Nosifer&display=swap');

:root {
    --basalt-secondary-color: 0, 0, 0;

    --basalt-overtone: 255, 0, 0;
    --basalt-undertone: 255, 0, 0;
    --basalt-bright-element-color: 255, 0, 0;
    --basalt-dark-element-color: 255, 0, 0;

    --link-color: 255, 0, 0;
    --link-visited-color: 255, 0, 0;
    --link-newpage-color: 255, 0, 0;

    --top-bar-link-color-hover: 255, 255, 255;
    --login-status-text-color-hover: 0, 0, 0;
    --side-bar-link-color-hover: 0, 0, 0;
    --side-bar-link-background-hover: 255, 255, 255;
    --selection-text-color: 0, 0, 0;

    --header-border-width: 0rem;
    --title-size: 1.25rem;
    --subtitle-size: 0rem;
    --side-bar-button-height: 50vh;
    --user-button-width: 2rem;
    --header-logo-size: 67.5%;

    --basalt-UI-dark-palette: 0, 0, 0;
    --top-bar-link-background-hover: 255, 0, 0;

}

#header h1 a::before {
    display: none;
}

#header h1 a span::after {
    margin-bottom: 0;
}

#container #header {
    background: none;
    position: absolute;
    box-shadow: unset;
    backdrop-filter: none;
}

div #container-wrap {
    background-color: #060C0C;
    background: url(https://7bye.com/hoah/i/2024/06/19/nccHXw6.jpeg);
    background-size: contain;
}

#main-content {
    margin-top: 0;
}
#page-content {
    padding-top: 0;
}

#page-content > p:nth-child(-n+4) {
    display: none;
}

.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    max-width: 100dvw !important;
}

.widebox .blockquote {
    margin: 4rem auto;
    padding: 2rem 2rem;
}

.headerimage {
    position: relative;
    height: calc(120dvh);
    background-image: url(https://7bye.com/hoah/i/2024/06/19/kJRzXtX.jpeg);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top;
}

.headerimage .wrathtext {
    position: relative;
    display: block;
    margin: auto;
    bottom: -75dvh;
    width: 40%;
}

.headerimage .skullseparator {
    position: absolute;
    bottom: -20dvw;
    width: 100%;
}

.darkWRATHbox {
    width: 100%;
    padding-top: 40dvh;
}

.darkWRATHboxinterior {
    width: 80%;
    margin: auto;
}

.darkWRATHbox h1 {
    color: red;
    font-family: "DM Mono", monospace;
    font-weight: 800;
    font-size: 8em;
    line-height: 1;
}

.darkWRATHbox h2 {
    color: red;
    font-family: "DM Mono", monospace;
    font-weight: 800;
    font-size: 8em;
    line-height: 1;
}

.darkWRATHboxinterior p {
    color: white;
    font-family: "DM Mono", monospace;
    font-weight: 800;
    font-size: 3em;
    line-height: 1;
}

.darkWRATHboxinterior h3 {
    color: white;
    font-family: "Playwrite IS", cursive;
    font-weight: 400;
    font-size: 4em;
    line-height: 1;
}

.darkWRATHboxinterior li {
    color: white;
    font-size: 1.5rem;
    font-family: "DM Mono", monospace;
    font-weight: 800;
}

.darkWRATHboxinterior li::marker {
    color: #444;
}

.darkWRATHboxinterior .listsmall li {
    font-size: 1.2rem;
}

.darkWRATHboxinterior li .linebreak {
    display: inline;
}

.darkWRATHboxinterior li .linebreak:before {
    content: "\A";
    display: block;
    line-height: 1em;
    white-space: pre;
}

.darkWRATHboxinterior2 {
    width: 60%;
}

.darkWRATHbox .sideskull {
    float: right;
    width: 50%;
}

h1, #page-options-container, .licensebox .collapsible-block-folded a, .page-watch-options a {
    color: white;
}

#action-area, .licensebox .collapsible-block-content {
    background: #0D0D0F;
    padding: 10px;
}

.licensebox .collapsible-block-link {
    background: #0D0D0F;
    padding: 10px;
}

/* Piece of shit sidebar */

#top-bar div.mobile-top-bar>.open-menu {
     visibility:visible;
     opacity: 0.6;
     transition: opacity 0.2s;
}

#top-bar div.mobile-top-bar>.open-menu:hover {
    opacity: 1;
}

#side-bar {
     margin-top:0;
     height:100%
}

#side-bar, #side-bar:is(:hover, :focus-within):not(:target) {
     left:calc(var(--side-bar-width) * -1)
}

#side-bar::after, #side-bar::before {
     display:none
}

#side-bar:target {
     left:0;
     width:var(--side-bar-width);
     z-index:10
}

#side-bar a.close-menu, #side-bar:target a.close-menu {
     display:block;
     pointer-events:none;
     position:fixed;
     top:0;
     right:0;
     left:auto;
     width:100%;
     min-width:3.75rem;
     height:100%;
     margin-left:0;
     opacity:0;
     z-index:-1;
     transition:width var(--side-bar-transition),opacity var(--side-bar-transition)   
}

#side-bar:target a.close-menu {
     width:calc(100% - var(--side-bar-width));
     pointer-events:auto;
     opacity:1;
}

/* Mobile */

@media only screen and (max-width: 1020px) {
    #container #header {
        background: black;
        position: sticky;
    }
    .headerimage {
        height: calc(80dvh);
        overflow-x: clip;
    }
    .headerimage .skullseparator {
        top: calc(55dvh);
        left: -37.5dvw;
        width: 150dvw;
        max-width: 150dvw;
    }
    .headerimage .wrathtext {
        bottom: -40dvh;
        width: 90%;
    }
    .darkWRATHbox {
        padding-top: 5dvh;
    }
    .darkWRATHboxinterior {
    width: 80%;
    margin: auto;
    font-size: 1rem;
    }
    .darkWRATHbox h1 {
        font-size: 4em;
    }
    .darkWRATHbox h2 {
        font-size: 4em;
    }
    .darkWRATHboxinterior p {
    color: white;
    font-family: "DM Mono", monospace;
    font-weight: 800;
    font-size: 2em;
    line-height: 1;
    }
    .darkWRATHboxinterior h3 {
    color: white;
    font-family: "Playwrite IS", cursive;
    font-weight: 400;
    font-size: 3em;
    line-height: 1;
    }
    .darkWRATHboxinterior2 {
    width: 100%;
    }
    .darkWRATHboxinterior li {
    color: white;
    font-size: 1.5rem;
    font-family: "DM Mono", monospace;
    font-weight: 800;
    }
    .darkWRATHbox .sideskull {
        display: none;
    }  
}
[[/module]]

