[[module css]]
.entry .description {
    display: none;
    position: absolute;
    background: black;
}

.entry .description .preview {
    margin-left: 0;
    margin: 0.5rem 1rem;
}

.entry:hover .description, .entry:active .description {
    display: block;
    animation-name: desc-bounce;
    animation-duration: 0.1s;
}

@keyframes desc-bounce {
    0% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-5px);
    }
    100% {
        transform: translateY(0);
    }
}

.entry-list {
    display: grid;
    margin-block: 2rem;
    list-style: none;
    font-size: 0.8rem;
}

.collapsible-block .entry-list {
    font-size: 1rem;
    margin-block: 1rem;
}

.entry {
    display: list-item;
    padding: .1rem;
    transition: background-color .175s ease-out;
}

.entry:is(:hover,:focus-within) {
    background-color: rgb(var(--basalt-undertone),.175);
}

.entry .title {
    font-size: 1.125em;
    font-style: italic;
}
.entry .title > p {
    margin: 0;
}
.entry .title :is(a,span) {
    font-weight: bold; font-style: normal;
}

.description > :not(.preview) {
    display: none !important;
}

.description > .preview {
    display: block;
    padding-block: .125rem;
    font-size: .875em;
    text-align: start;
    margin-inline-start: 1.5rem;
}
.preview > p {
    margin-block: .55em;
}

.preview > p:first-child {
    margin-block-start: .25em;
}
.preview > p:last-child {
    margin-block-end: .25em;
}

.entry-list li {
font-size: 1.125em;
}
