.noise {
    position: relative;
    white-space: nowrap;
    color: white;
    text-shadow:
    1px 1px 2px #000,
    -1px 1px 2px #000,
    1px -1px 2px #000,
    -1px -1px 2px #000;
}

.noise::after {
    content: "误";
    position: absolute;
    left: -0.5px;
    animation: noise-anim 2s infinite linear alternate-reverse;
    white-space: nowrap;
}

.noise::before {
    content: "失";
    position: absolute;
    left: -0.5px;
    animation: noise-anim 1.5s infinite linear;
    white-space: nowrap;
}

@keyframes noise-anim {
    0% {
        clip-path: inset(40% 1% 61% 0);
    }

    10% {
        clip-path: inset(92% 0 1% 1%);
    }

    20% {
        clip-path: inset(43% 0 1% 0);
    }

    30% {
        clip-path: inset(25% 1% 58% 1%);
    }

    40% {
        clip-path: inset(54% 0 7% 0);
    }

    50% {
        clip-path: inset(58% 1% 65% 0);
    }

    60% {
        clip-path: inset(40% 0 25% 0);
    }

    70% {
        clip-path: inset(94% 1% 57% 1%);
    }

    80% {
        clip-path: inset(8% 0 85% 0);
    }

    90% {
        clip-path: inset(18% 1% 45% 1%);
    }

    100% {
        clip-path: inset(50% 1% 50% 0);
    }
}
table.timeline {
  width: 100%;
  border-spacing: 0;
}
table.timeline td {
  vertical-align: top;
}

table.timeline td.left {
  font-size: 2rem;
  border-right: solid 1px #999;
  width: 30%;
