[[module css]]
/*
背景画像
*/

div#container-wrap {
  background: none !important;
}

/*
ヘッダ
*/

#header {
  padding-top: 2em;
  height: 130px;
  z-index: 10;
  background: url(http://scp-jp-sandbox2.wikidot.com/local--files/nanimono-demonai/q2.png) 10px 40px no-repeat, url(http://scp-jp-sandbox2.wdfiles.com/local--files/indonootoko/nisso1.png) calc(100% - 10px) calc(100% - 40px) no-repeat;
}

div #header h1 {
  margin-left: 0;
  width: 273px;
  top: 35px;
  min-height: 100px;
  position: absolute;
}

div #header h1 a {
  position: absolute;
  opacity: 0;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  font-size: 1px;
}

div #header h2 {
  display: none;
}

#top-bar {
  position: absolute;
  bottom: 0px;
  margin-top: 115px;
  height: auto;
  padding: 0;
  z-index: 20;
  font-size: 90%;
}

#top-bar ul {
  float: right;
  padding-right: : 5em;
}

#top-bar a {
  color: #b5cde2;
}

#top-bar ul li a {
  border-left: none;
  border-right: none;
}

#top-bar ul li.sfhover a, #top-bar ul li:hover a {
  color: #178cce;
  border-left: none;
  border-right: none;
}

#search-top-box-input {
  display: none;
}

#search-top-box-form input[type=submit] {
  border: none;
  border-radius: 0px;
  padding: 2px 5px;
  font-size: 90%;
  font-weight: bold;
  color: #178cce;
  background-color: #fff;
  background: none;
  box-shadow: none;
  cursor: pointer;
}

#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
  border: none;
  color: #b5cde2;
  text-shadow: none;
  background-color: #fff;
  background: none;
  box-shadow: none;
}

#search-top-box {
  position: absolute;
  top: auto;
  bottom: 23px;
  right: 8px;
  width: 250px;
  text-align: right;
}

#header::after {
  content: '';
  position: absolute;
  width: 100%;
  margin: 0 auto;
  margin-top: 130px;
  height: 100px;
  background: url(http://scp-jp-sandbox2.wikidot.com/local--files/nanimono-demonai/q3-min-min.png) top left repeat-x;
}

/*
コンテント
*/

/* ヘッダーにかぶせない */

#main-content {
  margin-top: 100px;
}

h1, #page-title {
  color: #000;
  font-family: "宋体", SimSun,"游明朝", YuMincho, "ヒラギノ明朝 ProN W3", "Hiragino Mincho ProN", "HG明朝E", "ＭＳ Ｐ明朝", "ＭＳ 明朝", serif;
}

a {
  color: #b5cde2;
}

#page-content a{
  color: #00A;
}
#page-content h2{
font-weight:bold;
}
#page-content h2:before{
  content:'■';
  color:#006837;
}

.page-rate-widget-box .rate-points {
    background-color: #178cce !important;
    border: solid 1px #178cce;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #b5cde2;
    border-top: solid 1px #178cce;
    border-bottom: solid 1px #178cce;
}
.page-rate-widget-box .cancel {
    background-color: #178cce;
    border: solid 1px #178cce;
}
.page-rate-widget-box a{
    color: #fff !important;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
    background: #b5cde2;
    color: #fffff0;
}
.page-rate-widget-box .cancel a:hover {
    background: #b5cde2;
}
#page-content .creditRate .page-rate-widget-box .rate-points {
     background-color: #178cce !important;
     border: solid 1px #0b65c0;
 }
 .rate-box-with-credit-button .creditButton{
 background-color: #178cce !important;
 }
