[[module CSS]]
 :root{
   --header-title: "绝对异类";
   --header-subtitle: "无需在意他人的看法";
   --titleColor: #cc9e00;
   --subtitleColor: #cc9e00;
   --lgurl: none;
}

body{
    background-color: #fff9e5;
}

div#container-wrap {
    background: url(http://darry.wdfiles.com/local--files/scp-image-source-address/yd-header-bg.png) top left repeat-x;
}

h1, #page-title {
    color: #e6b200;
}

a{
    color: #e6b200;
}
a:hover, #side-bar div.menu-item a:hover{
    color: #fff9e5;
    background-color: #b38b00;
    text-decoration: none;
}

#side-bar .side-block {
    border: 2px solid #806300;
    border-radius: 0;
}

#side-bar .side-block {
    background-color: #faf9ea;
    background-image: url(http://darry.wdfiles.com/local--files/scp-image-source-address/dove-5873619_640.png);
    background-size: 100%;
}

#side-bar .heading, #side-bar div.menu-item a{
    color: #806300;
    border-bottom-color: #806300;
}

#side-bar div.menu-item img {
    filter: hue-rotate(67deg) brightness(200%);
}

/* Tab */

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color:#664f00; /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #ffd585 ! important; /* tab background */
    border-color: #ffd585;
    color: #000000;
    transition: 0.125s;
}

.yui-navset .yui-nav a em {
background-color: #e6b200;
box-shadow: 0px 1px 2px rgba(179,139,0,0.7);
border-color: #6a0702;
color: #000;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-color: #ffd585; /* selected tab background */
    color: black;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: #cb8d58;
    box-shadow: 0px 1px 2px rgba(106,7,2,0.5);
    background-color: #fff1af;
    color: #6a0702;
}

.yui-navset .yui-content {
    background-color: #ffeeb3; /* content background color */
    padding: 1em 1em;
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: #ffd585; /* content border */
    border-top-color:#ffd585; /* different border color */
}

.yui-navset-left .yui-content {
    border-left-color: #b7a9a9; /* different border color */
}

blockquote {
    border-color: #43130D;
    background-color: #fff4cc;
}

/* ---- INFO PANE ---- */
 .rate-box-with-credit-button{
     background-color: hsl(268, 40%, 25%) !important;
     background-color: var(--very-dark-moderate-violet) !important;
     border-color: hsl(268, 40%, 25%) !important;
     border-color: var(--very-dark-moderate-violet) !important;
     border-radius: 0 !important;
     box-shadow: none !important;
}
 .rate-box-with-credit-button .fa-info{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
     border-color: hsl(268, 40%, 45%);
     border-color: var(--dark-moderate-violet);
}
 .rate-box-with-credit-button .fa-info:hover{
     color: hsl(112, 77%, 48%);
     color: var(--vivid-lime-green);
}
 .rate-box-with-credit-button .cancel{
     border: 0;
}
 .close-credits,
 .credit-back{
     filter: hue-rotate(268deg);
}
 .modalbox{
     box-shadow: 0 1px 10px hsla(282, 73%, 51%, .3);
}

/* ---- PAGE RATING ---- */
 .page-rate-widget-box{
     border: none;
     border-radius: 0;
     box-shadow: none;
}
 div.page-rate-widget-box .rate-points{
     background-color: #664f00 !important;
     border-radius: 0;
}
 .page-rate-widget-box .rateup,
 .page-rate-widget-box .ratedown{
     border-color: #664f00;
     background-color: #fff9e5;
}
 .page-rate-widget-box .rateup a:hover,
 .page-rate-widget-box .ratedown a:hover{
     color: #fff9e5;
     background-color: #664f00;
}
 .page-rate-widget-box .cancel{
     background-color: #664f00;
     color: #cc9e00;
     border-left: 0;
     border-radius: 0;
}
 .page-rate-widget-box .cancel a{
     color: #cc9e00;
}
 .page-rate-widget-box .cancel a:hover{
     color: #fff4cc;
     background-color: #664f00;
     border-radius: 0;
}

#login-status ul a {
     color: #b38b00;
}

/* ---- SEARCH BAR ---- */
 #search-top-box-input{
     background-color: #4d3b00;
     border: 1px solid #806300;
     border-radius: 0;
}
 #search-top-box-input:hover,
 #search-top-box-input:focus{
     background: #cc9e00;
     border-radius: 0;
}
 #search-top-box-form input[type="submit"]{
     color: #b38b00;
     background-color: #e6b200;
     background-image: none;
     border-radius: 0;
     box-shadow: none;

     font-size: unset;
}
 #search-top-box-form input[type="submit"]:hover,
 #search-top-box-form input[type="submit"]:focus{
     color: #e6b200;
     background-image: none;
     background-color: #b38b00;
     border-radius: 0;
     box-shadow: none;
}

/* ---- TOP MENU ---- */
 #top-bar ul li ul{
     border: solid 1px hsl(268, 40%, 25%);
     border: solid 1px var(--very-dark-moderate-violet);
}
 #top-bar a{
     color: #f1f1f1;
}
 #top-bar ul .sfhover a,
 #top-bar ul li:hover a{
     color: #4d3b00;
}
 #my-account,
 #account-topbutton{
     color: hsl(0, 0%, 95%);
     color: var(--very-light-grey);
}

.hovertip {
  border: 1px solid #cc9e00 !important;
  background-color: #fffdf7 !important;
}
[[/module]]

