div#header {
background: url(http://scp-jp-sandbox3.wdfiles.com/local--files/draft%3A2081491-39-c1cb/okame.png) 15px 40px no-repeat;
background-size: 95px 95px;
}
@media (max-width: 580px) {
div#header {
background-position: 12px 55px;
background-size: 60px 60px;
}
}
@media (max-width: 479px) {
div#header {
background-position: 0px 55px;
background-size: 60px 60px;
}
}
div#container-wrap {
background: url(http://scp-jp-sandbox3.wdfiles.com/local--files/draft%3A2081491-39-c1cb/yotutuji.png) top left repeat-x;
}
div#header h1 a span {
font-size: 0px;
}
div#header h1 a:before {
content: "四辻喜劇";
color: #eee;

}
div#header h2 span {
font-size:0px;
padding: 4px;
}
div#header h2:after {
content: "この世は舞台、人も妖も皆役者。";
font-weight: bold;
color: #f0f0c0;
padding: 19px 0;
text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
white-space: pre;
}
@media (max-width: 479px) {
div#search-top-box {
top: 65px;
}
}
[[/module]]

