    width: 100%;
    top: 0;
    position: absolute;
    background: url("http://scpsandboxcn.wikidot.com/local--files/forget-me-not/ALL-white");
    background-size: cover;
    background-position: 50% 50%;
} /* SPACE */

div#container-wrap {
    background-image: url(none);
}

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background-color: #ffffff;
    background-image: linear-gradient(to bottom,
      #ffffff,
      #ffffff);
} /* Red-top bar */

#header {
    background-image: url("http://scpsandboxcn.wikidot.com/local--files/collab:forget-me-not/dying-foundation")
}

#header h1 span {
    display: none;
}
#header h1 a::before {
    content: "SCP基金会";
    font-family: "Stardos Stencil", "Times New Roman", serif;
    font-weight: 700;
    color: #ede9e900;
    font-style: normal;
    text-shadow: 5px 5px 5px rgba(0,0,0,.1);
}

#header h1 a span{
    visibility: hidden;
}

#header h2 span {
    color: transparent;
    text-shadow: 0px 0px 0px;
}

#header h2 span::before {
    content: "勿忘我";
    color: #ede9e900;
    font-family: 'Exo', sans-serif;
    font-weight: 400;
    font-size: 120%;
    text-shadow: 4px 4px 4px rgba(0,0,0,.9);
}

/* Log-In Info */
#login-status {
    color: rgba(228, 228, 228);
}
#login-status a {
    background: transparent;
    color: rgba(228, 228, 228);
}

#login-status ul a {
    color: #ffffff;
    background: #cccccc;
    border-color: #ffffff;
}

#login-status ul a:hover {
   color: #d9d9d9;
   font-weight: bold;
}

#account-topbutton {
    background: transparent;
    color: rgba(228, 228, 228);
    border: none;
}

#account-options {
   border-color: #d9d9d9;
}

div#login-status {
    opacity: 0.1;
    filter: filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

/* Search Bar */
#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    border-color: #ffffff;
    background-color: #ffffff;
}

#search-top-box-input {
    color: #ffffff !important;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    color: #cccccc !important;
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border-color: #ffffff;
    background: #ffffff;
    color: rgba(228, 228, 228, 0.5);
}

/* TITLE */

h1,
#page-title {
    color: rgba(228, 228, 228, 0.5);
}

h1, h2, h3, h4, h5, h6,
#page-title {
    color: rgba(228, 228, 228);
}

#page-title {
    border-color: rgba(204, 204, 204, 0.5);
}

/* TOP-BAR SETTINGS */
#top-bar a {
    color: #ccc;
}

#top-bar ul li ul {
    border-color: #ffffff;
    border-radius: 0px 0px 10px 10px;
    overflow: hidden;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
   border-top-color: #ffffff;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: rgba(244, 244, 244, 0.8);
    color: #cccccc;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #f4f4f4; /* top bar hover background color */
    color: #cccccc;
}

/* SIDEBAR SETTINGS */
#side-bar .side-block {
    background-color: white !important;
    border-color: transparent;
    border-radius: 0px;
    box-shadow: none;
}

#side-bar .heading {
    color: grey;
    border-bottom: solid 1px grey;
}

@media (max-width: 767px) {
    #side-bar {
         background-color: white;
    }

    #side-bar:target .close-menu {
         background: none;
    }
}

#side-bar .collapsible-block-folded {
    background-image: url(http://scp-wiki.wdfiles.com/local--files/theme%3Aminimal/expand.png);
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px grey;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: grey;
}

/* Side-Bar Images */
#side-bar img,
iframe.scpnet-interwiki-frame {
    opacity: 0.1;
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

/* Side-Bar Collapsible Stuff */
#side-bar .collapsible-block-folded {
    background-image: none;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 2px #ffffff;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: rgba(255, 255, 255, 0.5);
}

/* Mobile Sidebar */
@media (max-width: 767px) {
    #side-bar {
        background-color: #f4f4f4;
    }

    .open-menu a {
        border-color: #f4f4f4 !important;
        background-color: rgba(222, 222, 222, 0) !important;
        color: #f4f4f4 !important;
    }
}

/* BODY */
body {
    background-color: #ffffff;
    background-image: linear-gradient(
        #ffffff,
        #ffffff 400px);
    color:  rgb(0, 0, 0);
}

/* FOOTER */
#footer {
    background: #ffffff;
    color: rgba(228, 228, 228, 0.5);
}

#footer a {
    color: rgba(228, 228, 228, 0.5);
}

#license-area {
     color: #f4f4f4;
}

/* Links */
a,
a.newpage,
a:visited,
#side-bar a:visited {
    color: rgba(204, 204, 204);
}

/* Selection */
::selection {
    background: rgba(228, 228, 228);
    color: #fff;
}

/* Footnotes */
.hovertip {
     background-color: #cccccc !important;
     border-color: #f4f4f4 !important;
}

.footnotes-footer .title {
    color: #cccccc;
}

/* Scrollbar */
::-webkit-scrollbar-thumb {
    background: #ffffff;
}

/* Pop-Up Windows */
.owindow {
    background-color: rgba(244, 244, 244);
    border-color: #ffffff;
}

.owindow .modal-header {
    background-color: rgba(244, 244, 244);
}

.owindow .modal-body img {
    background-color: transparent !important;
}

.owindow .title {
    background-color: rgba(244, 244, 244);
    color: #ffffff;
    border-bottom: 1px solid #ffffff;
}

.owindow .button-bar a {
    background-color: rgba(244, 244, 244);
    border-color: #ffffff;
}

.owindow .button-bar a:hover {
    background-color: rgba(244, 244, 244);
}

/* Edit Buttons */
.buttons .btn {
    background-color: #ffffff;
    border-color: #f4f4f4;
    color: #cccccc;
    padding: 3px 5px;
}

.buttons .btn:hover {
    background-color: #ffffff;

    color: #f4f4f4;

}
/* Edit Lock Info*/
#lock-info {
    background-color: #ffffff;
    border-color: #f4f4f4;
}

/* Close Button for Page Source, Rating, Etc */
a.action-area-close:hover {
    background-color: rgba(244, 244, 244);
}

/* Page-History Current */
.pager .current {
    background-color: rgba(204, 204, 204);
    border-color: #ffffff;
}

/* Code */
.code {
    border-color: #f4f4f4;
    background-color: transparent;
    font-size: 14px;
}

/* RATING AND INFO MODULES */
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #ffffff!important;
    border: solid #ffffff 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #ffffff!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #f4f4f4;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #f4f4f4!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #ffffff!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}

/* Credit Module */
#page-content .rate-box-with-credit-button {
    background-color: #f4f4f4;
    border-color: #cccccc;
    border-radius: 5px;
    box-shadow: 0px 0px 0px 2px #ffffff;
}
#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}

#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
#page-content .rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
}

#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}

#page-content .rate-box-with-credit-button .fa-info {
     color: #ffffff;
}
#page-content .rate-box-with-credit-button .fa-info:hover {
     color: #ffffff;
}

#page-content .modalbox {
     background: #ffffff !important;
     color: #ccc;
     box-shadow: 0px 0px 0px 2px #ffffff;
}

.close-credits,
.credit-back {
    filter: grayscale(100%) brightness(400%);
}

/* Ayer's Info Module */
:root {
    --barColour: #f4f4f4;
    --linkColour: #ffffff;
}

.info-container .collapsible-block-unfolded {
    border-color: rgba(228, 228, 228, 0.5);
}

/* TABS */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: transparent;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background: white;
    border-color: white;
    color: grey;
    transition: 0.125s;
}

.yui-navset .yui-nav a em {
    background: white;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
    border-color: white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: white;
    color: grey;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: transparent;
    box-shadow: 0px 1px 2px 0.5px rgba(0,0,0,0.5);
    background: white;
}

.yui-navset .yui-content {
    background: transparent;
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: transparent;
    border-top-color: transparent;
}

.yui-navset-left .yui-content {
    border-left-color: #b7a9a9;
}
/* BLOCKQUOTE */
.blockquote, blockquote {
    background-color: #ffffff;
    color: black;
    font-family: 'Oswald', sans-serif;
    border: 4px solid #f4f4f4;
}
#toc {
    border-color: #f4f4f4;
    color: #cccccc;
    background-color: #ffffff;
    box-shadow: 0em 0.063em 0.125em rgba(0, 0, 0, 0.1);
    padding: 0 1em;
}
/* PAGE-INFO */
#page-info {
    clear: both;
    text-align: right;
    font-size: 87%;
    margin: 2px 2px;
    color: #f4f4f4;
}
/* HOVERTIP */
.hovertip {
    display: none;
    width: 20em;
    border: 2px solid #f4f4f4 !important;
    background-color: #ffffff;
    z-index: 100;
    color: #ffffff;
}
