[[module CSS]]
/*
    Powered on 2020/02/28 15:22:18
    [2020 Wikidot Theme]
    Created by YUANNN
    CC BY-SA 3.0
*/

h1, #page-title {
    color: #941C1E; /*1级标题、页面标题颜色*/
}
a,a:visited {
    color: rgb(0, 0, 0);
}
div#container-wrap {
    background: url(http://scpsandboxcn.wikidot.com/local--files/yuannnzhe/bj2) top left repeat-x;
}
div#header {
    background: url(http://scpsandboxcn.wikidot.com/local--files/yuannnzhe/logo6) 15px 33px no-repeat;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "Dr.Yuannn的办公室";
    color: rgb(255, 255, 255);

}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: " 做人不要太攀比，除非会做白切鸡";
    font-weight: bold;
    color: rgb(255, 255, 255);
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}
div#side-bar div.side-block {
    background: #F0F8FF;
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(26,36,49);
    border-color: rgb(82,104,137);
}
div.yui-navset div.yui-content {
    background: #f3f3f3;
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #d4dce7;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #b3b4c1;
    color: #000000;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #55597f;
    color: rgb(255, 255, 255);
}
div.open-menu a {
    color: rgb(0, 0, 0)!important;
    border-color: rgb(0, 0, 0)!important;
}

/* 搜索栏色 */
#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:active {
    border: #30332c;
    color: #1a2431;
    background-color: #55597f;
}
#search-top-box-input:hover,#search-top-box-input:focus {
    border: solid 1px #311a1a;
    background-color: #e2948b;
}
#search-top-box-form input[type=submit] {
    border: solid 1px #30332C;
    color: #d8cfd5;
    background: #323760;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #ffffff;
    color: #fff5ff; 
    background-color: #f4e4ee;
    background: #212549;
}
#search-top-box-input {
   color: #fff5ff;
   opacity: 1.0;/* 这里好像没什么用，但是留一个代码存着.jpg */
}
#search-top-box-input:hover, #search-top-box-input:focus {
    background: #212549;
    border: 1px solid #212549;
    color: #212549;
    opacity: 1.0;
}

/* 链接色 */
a {
    color: #d28282;}
a.newpage {
    color: #DB7093;}
#side-bar a:visited{
    color: #616480;}
a:visited {
    color: #a15959;}
body {
    color: #0F135B; /*整个页面的文字基础颜色*/
}

/* 账号信息 */
#login-status {
    color: #f0e5c8;
}
#login-status a {
    color: #f0e5c8;
}
#login-status ul a {
    color: #f0e5c8;
}
#account-topbutton {
    background: #ccc;
    color: #f0e5c8;
}

/* 评分模组色 */
.page-rate-widget-box .rate-points {
  background-color: #1a2431 !important;
  border: solid 1px #1a2431;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #f0f8ff;
  border-top: solid 1px #1a2431;
  border-bottom: solid 1px #1a2431;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background: transparent;
  color: #1a2431;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #bfb6bb;
  color: #f0ffff;
}
.page-rate-widget-box .cancel {
  background: transparent;
  background-color: #1a2431;
  border: solid 1px #1a2431;
}
.page-rate-widget-box .cancel a {
  color: #f0ffff;
}
.page-rate-widget-box .cancel a:hover {
  background: #cdeadc;
  color: #f0ffff;
}

/* tab重新着色 */
.yui-navset .yui-content{
    background-color: #f3f3f3;/* 背景 */
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color:#d8d8d8;/* 未选择便签灰 */
    background-image: url(../images/yuitabs.png);
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* 可选择没有聚焦效果（不是很知道这是啥意思） */
.yui-navset .yui-nav .selected a:hover { /* 可选择没有悬停效果（同上） */
    background:#1a2431 url(../images/yuitabs.png) repeat-x left -1400px; /* 选中的便签的背景深色 */
    color:#fff;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background:#899E6A/* 鼠标在上的时候的浅色 */
url(../images/yuitabs.png) repeat-x left -1300px;
    text-decoration: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #d4dce7;/* 上边线色 */
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #b3b4c1;/* 好像是更细的边线 */
}
.yui-navset li {
    line-height: normal;
}

.vpoem{
text-indent: 0;
-webkit-writing-mode: vertical-rl;
writing-mode: vertical-rl;
margin: auto;
margin-bottom:0.5em;
font-size:2.5em;
font-family: 'KaiTi', 'MS PMincho', 'STKaiti', 'HiraMinProN-W6', 'Hiragino Kaku Gothic Pro', 'MS PGothic', serif;
}

.vpoem p{
line-height: 1.4;
margin:0 auto;
}

.vpoem tt{
    vertical-align: middle;
font-family: 'KaiTi', 'MS PMincho', 'STKaiti', 'HiraMinProN-W6', 'Hiragino Kaku Gothic Pro', 'MS PGothic', serif;
}

@media screen and (max-width: 767px) {
.vpoem{
font-size:1.5em;
}
}

.vpoem{
margin-top: 0.5em!important;
}

.scp-image-block {
    border-color: #941C1E; /*插图边框颜色*/
}

#main-content .page-tags {
    display: none;
}

div.pseudo-tags ul {
    font-size: 85%;
    border-top: 1px solid #BBB;
    display: inline-block;
    margin: 10px 0 0;
    padding: 0 2px;
    list-style: none;
}

div.pseudo-tags li {
    color: #b01;
    text-decoration: none;
    display: inline-block;
    margin: 0 1px;
    cursor: pointer;
}


