[[module CSS]]
#header h1 a {
   content: "SCP基金会";
   /* font-family: Lisu,STKaiti,Kaiti,SimSun,sans-serif; */
}

#header h1 a::before {
   content: "舒緩聲波製作組";
   margin-left: 10px;
}

#header h1 {
   padding: 0;
   float: left;
   max-height: 95px;
}

#header h1 a span{
   display: block;
   content: "";
   visibility: hidden;
}

#header h2 {
   margin-left: 13%;
   padding: 0;
   clear: left;
   float: left;
   font-size: 0%;
   max-height: 38px;
}

#header h2 span {
   display: block;
   margin: 0;
   padding: 19px 0;
   line-height: 0px;
   max-height: 0px;
   font-weight: bold;
   color: transparent;
   text-shadow: none;
}

#header h2 span::before {
   content: " 共 鳴 · 創 作 · 演 奏 ";
   display: block;
   margin: 0 0 0 63px;
   padding: 5px 0;
   line-height: 0px;
   max-height: 0px;
   font-weight: bold;
   color: #f0f0c0;
   text-shadow: 1px 1px 1px #000;
   text-shadow: 1px 1px 1px rgba(0,0,0,.8);
   font-size: 15px;
   /* font-family: STKaiti,Kaiti,SimSun,sans-serif; */
}

#page-title {
   /*font-family: STXingkai,Kaiti,SimSun,sans-serif,etinjat; */
   font-size: 200%;
   text-align: center;
}

#page-title::after {
/*    content: " 舉筆耕劃欲為冊"; */
}

/* Grid layout */

.left-grid {
   float: left;
   width: 49%;
   height: 110px;
}
.right-grid {
   float: right;
   width: 49%;
   height: 110px;
}

/* Mobile Media Query */
@media (max-width: 479px) {
.left-grid,
.right-grid {
   width: 100%;
   float: left;
}
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) {
.left-grid,
.right-grid {
   width: 100%;
   float: left;
}
}

/* Tablet Media */
@media (min-width: 768px) and (max-width: 979px) {
.left-grid,
.right-grid {
   width: 100%;
   float: left;
}
}


#main-content .page-tags {
   display: none;
}

div.pseudo-tags ul {
   font-size: 85%;
   border-top: 1px solid #BBB;
   display: inline-block;
   margin: 10px 0 0;
   padding: 0 2px;
   list-style: none;
}

div.pseudo-tags li {
   color: #b01;
   text-decoration: none;
   display: inline-block;
   margin: 0 1px;
   cursor: pointer;
}

.mobile-top-bar .open-menu a{
           border-style: solid;
           border-width: 0px 0px 100px 100px;
           border-color: transparent transparent black transparent !important;
           background-color:  transparent !important;
           width: 0px;
           height: 0px;
           border-radius:0;
}
.mobile-top-bar .open-menu a::after{
 content: "🎶";
}


.preview{
color:grey;
width:100%;
display: inline-block;
padding-left:1%;
transition: box-shadow 1s ease-out;
}

.preview:hover{
box-shadow: 0px 4px 10px 0 rgba(0, 0, 0, 0.19);
transition: box-shadow 0.3s ease-out;
}

.preview.original:hover, .preview.true:hover{
 background: url('https://zok6hoi2.wikidot.com/local--files/home:home/JochoiIcon.png') 90% 90% no-repeat;
 background-size: 12%;
}

.preview .span {
opacity: 0;
pointer-events: none;
transition: all 0.3s ease-in-out;
overflow:hidden;
text-overflow:ellipsis;
position:fixed;
z-index: 11;
right: 1em;
bottom: 2em;
width:21%;
width: calc(50vw - 520px);
min-width: 250px;
height:23%;
padding:1%;
float:top;
color:grey;
text-size:0.9em;
box-shadow: 0px 4px 8px 0 rgba(0, 0, 0, 0.2), 0px 6px 20px 0 rgba(0, 0, 0, 0.19);
font-family:微软细黑,Segoe UI Light,Verdana,sans-serif;
}

.preview:hover .span {
opacity: 1;
background-color:#ffffff;
}

.imagepre{
background:#fff;
max-width:19%;
max-height:40%;
position:fixed;
right: 2em;
bottom:calc(23% + 3em);
box-shadow: 0px 4px 8px 0 rgba(0, 0, 0, 0.2), 0px 6px 20px 0 rgba(0, 0, 0, 0.19);
}

.right-align {
   text-align: center !important;
}
.code pre {
  white-space: pre-wrap;
}
.code p {
  margin: revert;
}
:root {
  --mono-font: PT Mono,Andale Mono,Courier New,Courier,monospace;
}
