void displayArts();
void displayOthers();
void displayCharacters();

int main() {
　printf("Project 鑿海\n");
　printf("Version 0.1.0\n");
　printf("This program is released under AGPLv2. Copyright© Jochoi, 2018-2024\n");
　printf("\n");

　while (select) {
　　if (select!=-1) {
　　　printf("選擇您想要查看的內容。\n");
　　　printf("0: 退出\n")
　　　printf("1: 原創作品\n");
　　　printf("2: 翻譯作品\n");
　　　printf("3: 藝術作品\n");
　　　printf("4: 其他作品\n");
　　　printf("5: 角色設定\n");
　　};
　　printf("選擇: ");
　　scanf("%d", &select);

　　switch (select) {
　　　default: select=-1; break;
　　　case 0:
　　　case -1:
　　　　break;
　　　case 1: displayOriginals(); break;
　　　case 2: displayTranslations(); break;
　　　case 3: displayArts(); break;
　　　case 4: displayOthers(); break;
　　　case 5: displayCharacters(); break;
　　};
　};

　return 0;
};


